package TA04_01;

public class mainApp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double a = 1, b = 5;
		//Suma, Resta, Multiplicacion, Division, Modulo
		
		//Suma
		System.out.println("Suma: "+(a+b));
		
		//Resta
		System.out.println("Suma: "+(a-b));
		
		//Multiplicacion
		System.out.println("Suma: "+(a*b));
		
		//Division
		System.out.println("Suma: "+(a/b));
		
		//Modulo
		System.out.println("Suma: "+(a%b));
	}

}
